import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class CountriesComponent {
    constructor(http, baseUrl) {
        this.countries = [];
        this.details = [];
        this.teste = "teste";
        this.display = 'none';
        http.get(baseUrl + 'merchancountriesangular').subscribe(result => {
            this.countries = result;
        }, error => console.error(error));
    }
    getCurrency(country) {
        var teste = country.currencies[Object.keys(country.currencies).map(i => i).flat().toString()].name;
        if (!country.currencies == null) {
            teste = country.currencies[Object.keys(country.currencies).map(i => i).flat().toString()].name;
        }
        return teste;
    }
    getCurrencySymbol(country) {
        var teste = country.currencies[Object.keys(country.currencies).map(i => i).flat().toString()].name;
        if (!country.currencies == null) {
            teste = country.currencies[Object.keys(country.currencies).map(i => i).flat().toString()].name;
        }
        return teste;
    }
    openModal(country) {
        this.display = 'block';
        this.details = country;
    }
    onCloseHandled() {
        this.display = 'none';
    }
}
